import React from 'react'
import {Line} from 'react-chartjs-2';
import myData from '../utils/cdata.json';

import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    Filler,
  } from 'chart.js';
  
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend,Filler);



//configurations
const config = {
      type: 'line',
      data : data,
      options: {
        responsive: true,
        title:{
          display:true,
          text:"phd database"
        },
        plugins: {
          tooltip: {
            mode: 'index'
          },
        },
        interaction: {
          mode: 'nearest',
          axis: 'x',
          intersect: false
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Years',
            }
          },
          y: {
            stacked:true,
            title: {
              display: true,
              text: 'Count'
            }
          }
          
        }
      }
  };




function ThesesChart(){


  return(
    <div>
    <Line
    data={data}
    options={config}
    />
    </div>
  );
}

export default ThesesChart
