//             },
//             stacked: true
//           }]
//         },
//         ticks: {
//           min: 0,
//           max:1500,
//           stepSize: 1000
//         }
//     },
//   };



function ThesesChart({chartData}){

  useEffect(()=>{
    Chart.options
  },[])




  return(
   <Line 
   data={chartData}
   options={
    {
      type: 'line',
      data: {chartData},
      options: {
        responsive: true,
        plugins: {
          title: {
            display: true,
            text: "Phd Dataset Visualization"
          },
          tooltip: {
            mode: 'index'
          },
        },
        interaction: {
          mode: 'nearest',
          axis: 'x',
          intersect: false
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Years'
            }
          },
          y: {
            stacked: true,
            title: {
              display: true,
              text: 'Count'
            }
          }
        }
      }
   }
  }/>
  );
}

export default ThesesChart
