import React, { useEffect } from 'react'
import {Line} from 'react-chartjs-2';


import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    Filler,
    Chart,
  } from 'chart.js';
  
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend,Filler);



var config = {
      type: 'line',
      options: {
        responsive: true,
        plugins: {
          title: {
            display: true,
            text: "Phd Dataset Visualization"
          },
          tooltip: {
            mode: 'index'
          },
        },
        interaction: {
          mode: 'nearest',
          axis: 'x',
          intersect: false
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Years',
              padding: {
                top: 10,
                bottom: 30
            }
            }
          },
          y: {
            stacked: true,
            title: {
              display: true,
              text: 'Count'
            }
          }
        }
      }
  };



function ThesesChart({chartData}){

use

 const chart = new Chart(chartData,{
  config
 })


  return(
    chart
  );
}

export default ThesesChart
